void ssd1309_TestBorder() {
    ssd1309_Fill(Black);
   
    uint32_t start = get_time();
    uint32_t end = start;
    uint8_t x = 0;
    uint8_t y = 0;
    do {
        ssd1309_DrawPixel(x, y, Black);

        if((y == 0) && (x < 127))
            x++;
        else if((x == 127) && (y < (SSD1309_HEIGHT-1)))
            y++;
        else if((y == (SSD1309_HEIGHT-1)) && (x > 0)) 
            x--;
        else
            y--;

        ssd1309_DrawPixel(x, y, White);
       // printf("x : %d, y : %d\n",x,y);
        ssd1309_UpdateScreen();
    
        usleep(5);
        end = get_time();
       //  printf("end : %d, start : %d, diff : %d\n",end,start, (end-start));
    } while((end - start) < 26318950);
   
    usleep(1000);
}


