void ssd1309_TestFPS() {
    ssd1309_Fill(Black);
   
    uint32_t start = get_time();
    uint32_t end = start;
    int fps = 0;
    char message[] = "ABCDEFGHIJK";
   
    ssd1309_SetCursor(2,0);
    ssd1309_WriteString("Testing...", Font_11x18, White);
    ssd1309_SetCursor(2, 18*2);
    ssd1309_WriteString("0123456789A", Font_11x18, White);
   
    do {
        ssd1309_SetCursor(2, 18);
        ssd1309_WriteString(message, Font_11x18, Black);
        ssd1309_UpdateScreen();
       
        char ch = message[0];
        memmove(message, message+1, sizeof(message)-2);
        message[sizeof(message)-2] = ch;

        fps++;
        end = get_time();
    } while((end - start) < 500000000);
   
    usleep(5000);

    char buff[64];
    printf("end : %d, start : %d, diff : %d, fps : %d\n",end,start, (end-start), fps );
    fps = (float)fps / (((end - start)*0.013) / 1000000.0);
   
    sprintf(buff, "~%d FPS", fps);
    
    ssd1309_Fill(Black);
    ssd1309_SetCursor(2, 2);
    ssd1309_WriteString(buff, Font_11x18, White);
    ssd1309_UpdateScreen();
}

